$(function () {
	$.datepicker.setDefaults($.datepicker.regional['tr']);
	$("#baslangicTarih").datepicker({
		language: "TR",
		changeMonth: true,
		changeYear: true,
		showAnim: "fold",
		monthNames: ['Ocak', 'Şubat', 'Mart', 'Nisan', 'Mayıs', 'Haziran', 'Temmuz', 'Ağustos', 'Eylül', 'Ekim', 'Kasım', 'Aralık'],
		monthNamesShort: ['Oca', 'Şub', 'Mar', 'Nis', 'May', 'Haz', 'Tem', 'Ağu', 'Eyl', 'Ekm', 'Kas', 'Ara'],
		dayNames: ['Pazar', 'Pazartesi', 'Salı', 'Carşamba', 'Perşembe', 'Cuma', 'Cumartesi'],
		dayNamesShort: ['Pz', 'Pt', 'Sa', 'Ça', 'Pr', 'Cm', 'Ct'],
		dayNamesMin: ['Pz', 'Pt', 'Sa', 'Ça', 'Pr', 'Cm', 'Ct'],

		dateFormat: 'dd.mm.yy',
		firstDay: 1,


	});

});
